-----------find the price for total order item--------
select product.price * order_item.order_item_quantity as price from product, order_item where product.PID = order_item.PID

-----------find the total amount of orders-----
select sum(oi.price) as total_amount,oi.OID from order_item as oi inner join product as p on oi.PID = P.PID group by oi.OID

-------------payment amount(including 10% tax)---------------
select total_amount+(total_amount * 0.1) as amount, OID from [order] 


----------------Average order value------------------------------------
SELECT avg(order_item.order_item_quantity * product.price) AS Average_order_value, order_item.OID FROM order_item
JOIN product ON order_item.PID= product.PID group by OID

